import {AfterContentInit, AfterViewInit, Component, ContentChildren, DoCheck, Input, QueryList} from '@angular/core';

@Component({
  selector: 'pf-tab',
  template: `
    <div *ngIf="active" class="tab-content">
      <ng-content></ng-content>
    </div>
  `
})
export class TabComponent {
  @Input() title: string;
  active = false;
  constructor () {
  }
}

@Component({
  selector: 'pf-tabs',
  templateUrl: './tabs.component.html',
  styleUrls: ['./tabs.component.css']
})
export class TabsComponent implements AfterContentInit {
  @ContentChildren(TabComponent) tabs: QueryList<TabComponent>;

  constructor() { }

  ngAfterContentInit() {
    setTimeout(() => {
       this.tabs.first.active = true;
    });
  }

  activate(activeTab) {
    for (const tab of this.tabs.toArray()) {
      tab.active = false;
    }
    activeTab.active = true;
  }

}
